from functools import lru_cache
from itertools import groupby

import gitlab
from gitlab.v4.objects import Project


@lru_cache(maxsize=None)
def get_gitlab(url, api_token, verifyssl):
    return gitlab.Gitlab(url, private_token=api_token, ssl_verify=verifyssl)


@lru_cache(maxsize=None)
def get_project(url, api_token, proj_id, verifyssl) -> Project:
    return get_gitlab(url, api_token, verifyssl).projects.get(proj_id)


# https://mermaid-js.github.io/mermaid-live-editor/#
def styles():
    return '''
classDef failed fill:white,stroke:#db3b21,color:black;
classDef success fill:white,stroke:#1aaa55,color:black;
classDef warning fill:white,stroke:#fc9403,color:black;
classDef skipped fill:white,stroke:#999,color:black;
'''


def job_names(jobs):
    text = '\n'
    for job in jobs:
        text += f"{job.id}({job.name})\n"
    return text


def job_statuses(jobs):
    text = '\n'
    for job in jobs:
        class_status = 'warning' if job.status == 'failed' and job.allow_failure == True else job.status
        text += f"class {job.id} {class_status}\n"
    return text


def job_flow_charts(jobs):
    text = '\n'
    last_stage_groups = []
    for k, g in groupby(jobs, key=lambda j: j.stage):
        stage_groups = list(g)
        for job in stage_groups:
            for prev_job in last_stage_groups:
                text += f'{prev_job.id} --> {job.id}\n'
        last_stage_groups = stage_groups
    return text


def pipeline_to_mermaid(jobs):
    jobs.sort(key=lambda k: k.id)
    scheme = 'graph LR\n'
    scheme += styles()
    scheme += job_names(jobs)
    scheme += job_flow_charts(jobs)
    scheme += job_statuses(jobs)
    return f'```mermaid\n{scheme}```'